import{_ as e,a as o}from"./chunks/OtherThemesUserMenus.jzfExzta.js";import{_ as i,c as s,o as a,a6 as n}from"./chunks/framework.dp3vP9A2.js";const _=JSON.parse('{"title":"Funciones del Lado del Usuario - Transformando Sitios Estáticos en Plataformas Dinámicas","description":"","frontmatter":{},"headers":[],"relativePath":"es/guide/user-overview.md","filePath":"es/guide/user-overview.md","lastUpdated":1737127002000}'),t={name:"es/guide/user-overview.md"},r=n('<h1 id="funciones-del-lado-del-usuario-transformando-sitios-estaticos-en-plataformas-dinamicas" tabindex="-1">Funciones del Lado del Usuario - Transformando Sitios Estáticos en Plataformas Dinámicas <a class="header-anchor" href="#funciones-del-lado-del-usuario-transformando-sitios-estaticos-en-plataformas-dinamicas" aria-label="Permalink to &quot;Funciones del Lado del Usuario - Transformando Sitios Estáticos en Plataformas Dinámicas&quot;">​</a></h1><p>VpAdmin te permite transformar sitios web estáticos en plataformas dinámicas al integrar sin problemas un conjunto de funciones centradas en el usuario. Cuando habilitas la funcionalidad de <strong>Admin</strong> al crear tu sitio web, tu sitio se integrará automáticamente con la autenticación de usuarios, la gestión de pedidos y las funciones de pago, proporcionando a tus visitantes una experiencia más interactiva y rica en funciones.</p><h2 id="funciones-clave-del-lado-del-usuario" tabindex="-1">Funciones Clave del Lado del Usuario <a class="header-anchor" href="#funciones-clave-del-lado-del-usuario" aria-label="Permalink to &quot;Funciones Clave del Lado del Usuario&quot;">​</a></h2><p>VpAdmin proporciona las siguientes funciones clave del lado del usuario para tu sitio web:</p><ul><li><p><strong>Autenticación de Usuario:</strong></p><ul><li><strong>Inicio de Sesión/Registro:</strong> Los usuarios pueden iniciar sesión o registrarse utilizando su dirección de correo electrónico, contraseña o cuentas de redes sociales a través de plataformas como Google, Facebook, GitHub y GitLab.</li><li><strong>Recuperación de Contraseña:</strong> Los usuarios pueden restablecer fácilmente sus contraseñas si es necesario.</li></ul></li><li><p><strong>Gestión de Pedidos:</strong></p><ul><li><strong>Historial de Pedidos:</strong> Los usuarios pueden ver los detalles de sus compras anteriores, el estado del pago y los mensajes del administrador del sitio.</li><li><strong>Seguimiento del Estado:</strong> Los usuarios pueden rastrear el estado de sus pedidos.</li></ul></li><li><p><strong>Gestión del Perfil de Usuario:</strong></p><ul><li><strong>Ver Perfil:</strong> Los usuarios pueden ver y gestionar su información personal.</li><li><strong>Resumen de Datos Personales:</strong> Los usuarios pueden ver la información clave de su cuenta.</li></ul></li><li><p><strong>Soporte de Pagos Integrado:</strong></p><ul><li><strong>Botones de Soporte Mejorados:</strong> Los usuarios pueden acceder a las opciones de pago utilizando botones de soporte mejorados colocados en tu sitio.</li><li><strong>Monetización:</strong> Tus usuarios pueden completar rápidamente y fácilmente las transacciones de pago, permitiéndote monetizar tu contenido.</li></ul></li></ul><div class="info custom-block"><p class="custom-block-title">Temas de VitePress</p><video width="1080" height="768" muted autoplay loop><source src="'+e+'" type="video/mp4"></video></div><div class="info custom-block"><p class="custom-block-title">Temas de Hugo, Jekyll</p><video width="1080" height="768" muted autoplay loop><source src="'+o+'" type="video/mp4"></video></div><h2 id="beneficios" tabindex="-1">Beneficios <a class="header-anchor" href="#beneficios" aria-label="Permalink to &quot;Beneficios&quot;">​</a></h2><ul><li><strong>Fácil de usar para los Usuarios:</strong> Los usuarios obtienen una experiencia de inicio de sesión, registro y pago sin problemas en tu sitio web.</li><li><strong>Gestión Fácil para el Administrador:</strong> Obtienes un enfoque simplificado para gestionar usuarios, pedidos y pagos dentro de tus sitios web.</li><li><strong>Conversión de Estático a Dinámico:</strong> Con VpAdmin, puedes convertir rápidamente cualquier sitio web estático en una plataforma interactiva.</li><li><strong>Capacidades de Monetización:</strong> Ahora puedes monetizar tu contenido más fácilmente y de forma segura utilizando un potente panel de control de administrador.</li><li><strong>Compromiso Mejorado del Usuario:</strong> Proporcionar una experiencia fluida y profesional, aumentando la probabilidad de que los visitantes se conviertan en usuarios recurrentes.</li></ul><p>Al activar la función de administrador al crear tu sitio web en VpAdmin, te beneficias instantáneamente de todas estas funciones y creas una plataforma dinámica que facilita la interacción, el compromiso y la funcionalidad centrada en el usuario.</p>',10),d=[r];function l(u,c,p,m,g,f){return a(),s("div",null,d)}const h=i(t,[["render",l]]);export{_ as __pageData,h as default};
