import{_ as s}from"./chunks/dashboard.rbI9-S5F.js";import{_ as a,c as r,I as n,w as i,a6 as e,j as t,E as l,o as g}from"./chunks/framework.dp3vP9A2.js";const x=JSON.parse('{"title":"نمای کلی داشبورد پایه","description":"","frontmatter":{},"headers":[],"relativePath":"fa/guide/basic-dashboard.md","filePath":"fa/guide/basic-dashboard.md","lastUpdated":1737127002000}'),d={name:"fa/guide/basic-dashboard.md"},p=e('<h1 id="نمای-کلی-داشبورد-پایه" tabindex="-1">نمای کلی داشبورد پایه <a class="header-anchor" href="#نمای-کلی-داشبورد-پایه" aria-label="Permalink to &quot;نمای کلی داشبورد پایه&quot;">​</a></h1><p>داشبورد پایه VpAdmin نمای کلی سریعی از معیارهای کلیدی و شاخص‌های عملکرد وب‌سایت شما ارائه می‌دهد و به شما امکان می‌دهد رشد و فعالیت آن را در یک نگاه نظارت کنید.</p><p><strong>معیارهای کلیدی:</strong></p><ul><li><strong>مجموع سایت‌ها:</strong> تعداد کل وب‌سایت‌هایی که در حال حاضر از طریق حساب VpAdmin شما مدیریت می‌شوند را نمایش می‌دهد.</li><li><strong>مجموع کاربران:</strong> تعداد کل کاربران ثبت نام شده در تمام سایت‌های شما را نشان می‌دهد.</li><li><strong>مجموع سفارش‌ها:</strong> تعداد کل سفارش‌های ثبت شده در تمام سایت‌های شما را نشان می‌دهد، اگر عملکرد پرداخت را ادغام کرده باشید.</li><li><strong>درآمد ماهانه:</strong> کل درآمد حاصل از وب‌سایت‌های شما برای ماه جاری را نمایش می‌دهد.</li><li><strong>میانگین مبلغ سفارش:</strong> میانگین مبلغ هر سفارش را نشان می‌دهد و بینشی در مورد رفتار هزینه مشتری ارائه می‌کند.</li></ul><p><strong>نمودارهای روند:</strong></p><p>داشبورد شامل نمودارهای روند تعاملی است که عملکرد وب‌سایت شما را در طول زمان تجسم می‌کند.</p><ul><li><strong>روند رشد کاربر:</strong> افزایش کاربران ثبت نام شده را در یک دوره پیگیری کنید.</li><li><strong>روند سفارش:</strong> تعداد سفارش‌های ثبت شده را در طول زمان نظارت کنید، که به شما در شناسایی روندها و الگوها کمک می‌کند.</li><li><strong>روند درآمد:</strong> ببینید چگونه درآمد ماهانه شما در طول زمان تغییر می‌کند.</li></ul><p><strong>ویژگی‌های تعاملی:</strong></p><ul><li><strong>به‌روزرسانی داده‌ها:</strong> از دکمه &quot;به‌روزرسانی داده‌ها&quot; برای به‌روزرسانی همه معیارهای نمایش داده شده با آخرین اطلاعات استفاده کنید.</li><li><strong>پیش‌نمایش تجزیه و تحلیل بیشتر:</strong> روی دکمه &quot;پیش‌نمایش داده‌های تجزیه و تحلیل بیشتر&quot; کلیک کنید تا گزینه‌های تجزیه و تحلیل پیشرفته را بررسی کنید.</li><li><strong>برو به سایت‌های خود:</strong> روی دکمه &quot;برو به سایت‌های خود&quot; کلیک کنید تا مستقیماً به بخش مدیریت وب‌سایت خود بروید.</li></ul><p><strong>اسکرین‌شات داشبورد:</strong></p>',10),_=t("a",{href:"/vpadmin/dashboard.png",class:"zoomable-image"},[t("img",{src:s,alt:"اسکرین‌شات داشبورد پایه"})],-1),c=t("p",null,"داشبورد پایه به گونه‌ای طراحی شده است که بینش‌های ضروری عملکرد را در قالبی کاربرپسند ارائه دهد و به شما امکان می‌دهد شاخص‌های کلیدی را نظارت کرده و در مورد حضور آنلاین خود تصمیمات آگاهانه بگیرید.",-1);function u(m,h,f,b,q,P){const o=l("MagnificPopup");return g(),r("div",null,[p,n(o,null,{default:i(()=>[_]),_:1}),c])}const A=a(d,[["render",u]]);export{x as __pageData,A as default};
