import{_ as n}from"./chunks/OrdersManagement.DaRRegY6.js";import{_ as a,c as e,I as r,w as s,j as o,a as i,a6 as l,E as d,o as p}from"./chunks/framework.dp3vP9A2.js";const U=JSON.parse('{"title":"مدیریت سفارشات و ادغام با پلتفرم‌های پرداخت","description":"","frontmatter":{},"headers":[],"relativePath":"fa/guide/orders-management.md","filePath":"fa/guide/orders-management.md","lastUpdated":1737127002000}'),m={name:"fa/guide/orders-management.md"},g=o("h1",{id:"مدیریت-سفارشات-و-ادغام-با-پلتفرم‌های-پرداخت",tabindex:"-1"},[i("مدیریت سفارشات و ادغام با پلتفرم‌های پرداخت "),o("a",{class:"header-anchor",href:"#مدیریت-سفارشات-و-ادغام-با-پلتفرم‌های-پرداخت","aria-label":'Permalink to "مدیریت سفارشات و ادغام با پلتفرم‌های پرداخت"'},"​")],-1),c=o("p",null,"VpAdmin یک سیستم مدیریت سفارش قدرتمند ارائه می‌دهد که به‌طور یکپارچه با پلتفرم‌های محبوب سازندگان مانند Ko-fi و Buy Me a Coffee ادغام می‌شود، به شما این امکان را می‌دهد تا گردش کار خود را ساده کرده و سفارشات خود را از یک داشبورد متمرکز مدیریت کنید. این مقاله اصول اساسی این ادغام و نحوه استفاده از قابلیت‌های VpAdmin را حتی بدون انتشار وب‌سایت خود توضیح می‌دهد.",-1),h=o("h2",{id:"اصول-ادغام",tabindex:"-1"},[i("اصول ادغام "),o("a",{class:"header-anchor",href:"#اصول-ادغام","aria-label":'Permalink to "اصول ادغام"'},"​")],-1),u=o("p",null,"ادغام VpAdmin با پلتفرم‌هایی مانند Ko-fi مبتنی بر مکانیزم Webhook است. در اینجا شرحی از این فرآیند آمده است:",-1),_=o("p",null,[o("strong",null,"نمودار زیر این فرآیند را نشان می‌دهد:")],-1),f=o("a",{href:"/vpadmin/OrdersManagement.png",class:"zoomable-image"},[o("img",{src:n,alt:"فرآیند مدیریت سفارشات"})],-1),V=l('<ol><li><strong>ایجاد یک وب‌سایت با قابلیت مدیریت:</strong> می‌توانید یک وب‌سایت در VpAdmin ایجاد کنید (یا یک «وب‌سایت مجازی» که بعداً در این مقاله توضیح داده شده است) با قابلیت مدیریت فعال، و دکمه‌های پشتیبانی را به وب‌سایت خود اضافه کنید.</li><li><strong>پیکربندی تنظیمات پرداخت:</strong> در VpAdmin، به تنظیمات &quot;پرداخت‌ها&quot; بروید و پیکربندی‌های پرداخت خود را اضافه کنید. این شامل اتصال حساب Ko-fi شما و تنظیم جزئیات لازم است.</li><li><strong>تنظیم URL Webhook:</strong> در حساب Ko-fi خود، URL Webhook را طوری پیکربندی کنید که به VpAdmin اشاره کند. این URL مخصوص وب‌سایت شما است و شامل شناسه مشتری وب‌سایت شما است (مثلاً، <code>https://api.vpadmin.org/prod-api/vpadmin/orders/callback/VpadminClientID</code>).</li><li><strong>کاربر پرداخت را آغاز می‌کند:</strong><ul><li>کاربر از وب‌سایت شما یا هر محتوایی که دکمه پشتیبانی شما را دارد بازدید می‌کند و برای پرداخت در Ko-fi روی دکمه پشتیبانی کلیک می‌کند.</li><li>کاربر پرداخت را در Ko-fi تکمیل می‌کند. VpAdmin تأیید پرداخت کاربر را در صفحه &quot;تأیید پرداخت‌ها&quot; ثبت می‌کند.</li></ul></li><li><strong>راه‌اندازی Webhook:</strong> پس از پرداخت موفقیت‌آمیز، Ko-fi، URL Webhook پیکربندی شده را راه‌اندازی کرده و جزئیات سفارش را به VpAdmin ارسال می‌کند.</li><li><strong>پردازش سفارش:</strong> VpAdmin اطلاعات سفارش را دریافت می‌کند، آن را پردازش کرده و بر اساس پیکربندی شما، اقداماتی مانند: <ul><li><strong>تطبیق خودکار:</strong> VpAdmin سعی می‌کند سفارش را با قصد پرداخت ثبت شده در مرحله 4 مطابقت دهد.</li><li><strong>به‌روزرسانی وضعیت:</strong> وضعیت سفارش در VpAdmin به‌روزرسانی می‌شود (مثلاً، &quot;موفقیت‌آمیز&quot;، &quot;ناموفق&quot;، &quot;در انتظار&quot;).</li><li><strong>پاسخ خودکار ایمیل:</strong> در صورت پیکربندی، VpAdmin یک پاسخ ایمیل خودکار برای کاربر ارسال می‌کند.</li><li><strong>بررسی دستی:</strong> اگر تطبیق خودکار ناموفق باشد یا پیکربندی نشده باشد، مدیران می‌توانند به‌طور دستی وضعیت سفارش را در صفحه &quot;تأیید پرداخت‌ها&quot; بررسی و به‌روزرسانی کنند.</li></ul></li></ol><h2 id="استفاده-از-vpadmin-برای-مدیریت-سفارش-بدون-یک-وب‌سایت-منتشر-شده-وب‌سایت-مجازی" tabindex="-1">استفاده از VpAdmin برای مدیریت سفارش بدون یک وب‌سایت منتشر شده (وب‌سایت مجازی) <a class="header-anchor" href="#استفاده-از-vpadmin-برای-مدیریت-سفارش-بدون-یک-وب‌سایت-منتشر-شده-وب‌سایت-مجازی" aria-label="Permalink to &quot;استفاده از VpAdmin برای مدیریت سفارش بدون یک وب‌سایت منتشر شده (وب‌سایت مجازی)&quot;">​</a></h2><p>حتی اگر وب‌سایتی از طریق VpAdmin منتشر نکرده‌اید، باز هم می‌توانید با ایجاد یک «وب‌سایت مجازی» در پلتفرم، از قابلیت‌های مدیریت سفارش آن استفاده کنید. این به شما امکان می‌دهد سفارش‌ها را از پلتفرم‌هایی مانند Ko-fi یکپارچه کنید و آنها را در یک مکان مدیریت کنید.</p><p><strong>در اینجا نحوه عملکرد آن آمده است:</strong></p><ol><li><strong>ایجاد یک &quot;وب‌سایت مجازی&quot;:</strong> در VpAdmin، یک وب‌سایت جدید ایجاد کنید. این وب‌سایت نیازی به دسترسی عمومی یا داشتن هیچ محتوای واقعی ندارد. این وب‌سایت به عنوان یک ظرف برای پیکربندی‌های پرداخت و مدیریت سفارش شما عمل می‌کند.</li><li><strong>فعال کردن قابلیت مدیریت:</strong> اطمینان حاصل کنید که قابلیت مدیریت برای این وب‌سایت مجازی فعال است. این به شما یک شناسه مشتری ارائه می‌دهد که برای پیکربندی Webhook بسیار مهم است.</li><li><strong>پیکربندی تنظیمات پرداخت:</strong> تنظیمات پرداخت Ko-fi خود را به این وب‌سایت مجازی اضافه کنید، درست مانند کاری که برای یک وب‌سایت معمولی انجام می‌دهید.</li><li><strong>تنظیم URL Webhook:</strong> در حساب Ko-fi خود، URL Webhook را طوری تنظیم کنید که به URL callback وب‌سایت مجازی شما، از جمله شناسه مشتری، اشاره کند.</li><li><strong>مدیریت سفارشات:</strong> اکنون، وقتی کاربران از طریق لینک‌های Ko-fi شما پرداخت می‌کنند، جزئیات سفارش به VpAdmin ارسال می‌شود. سپس می‌توانید این سفارش‌ها را از طریق صفحه &quot;تأیید پرداخت‌ها&quot; مدیریت کنید، وضعیت آنها را به‌روزرسانی کنید و در صورت لزوم پاسخ ارسال کنید.</li></ol><p>با استفاده از یک وب‌سایت مجازی، می‌توانید مدیریت سفارش را متمرکز کنید، پاسخ‌های ایمیل را خودکار کنید و بینش‌های ارزشمندی در مورد تراکنش‌های Ko-fi خود در پلتفرم VpAdmin به دست آورید.</p><h2 id="مزایای-استفاده-از-vpadmin-برای-مدیریت-سفارش" tabindex="-1">مزایای استفاده از VpAdmin برای مدیریت سفارش <a class="header-anchor" href="#مزایای-استفاده-از-vpadmin-برای-مدیریت-سفارش" aria-label="Permalink to &quot;مزایای استفاده از VpAdmin برای مدیریت سفارش&quot;">​</a></h2><ul><li><strong>مدیریت متمرکز:</strong> سفارشات را از پلتفرم‌های مختلف در یک مکان مدیریت کنید.</li><li><strong>گردش کار خودکار:</strong> پردازش سفارشات و پاسخ‌های ایمیل را خودکار کنید.</li><li><strong>بهبود کارایی:</strong> گردش کار خود را ساده کرده و در وقت صرفه‌جویی کنید.</li><li><strong>بهبود تجربه کاربری:</strong> تجربه‌ای حرفه‌ای‌تر و پاسخگوتر برای حامیان خود ارائه دهید.</li><li><strong>بینش داده‌ها:</strong> بینشی در مورد فروش و رفتار مشتری خود به دست آورید.</li></ul><h2 id="نتیجه-گیری" tabindex="-1">نتیجه گیری <a class="header-anchor" href="#نتیجه-گیری" aria-label="Permalink to &quot;نتیجه گیری&quot;">​</a></h2><p>سیستم مدیریت سفارش VpAdmin یک راه حل قدرتمند برای سازندگانی ارائه می‌دهد که از پلتفرم‌هایی مانند Ko-fi استفاده می‌کنند. چه یک وب‌سایت منتشر شده داشته باشید یا نه، می‌توانید از VpAdmin برای ساده‌سازی گردش کار خود، خودکار کردن وظایف و بهبود فرآیند کلی مدیریت سفارش خود استفاده کنید. با ادغام با این پلتفرم‌ها و ارائه ابزارهایی مانند پاسخ‌های خودکار و پیگیری متمرکز سفارش‌ها، VpAdmin به شما این امکان را می‌دهد که بر کار خلاقانه خود تمرکز کنید و در عین حال تجربه‌ای بی‌نقص را برای حامیان خود فراهم کنید.</p>',10);function A(q,b,k,K,P,v){const t=d("MagnificPopup");return p(),e("div",null,[g,c,h,u,_,r(t,null,{default:s(()=>[f]),_:1}),V])}const W=a(m,[["render",A]]);export{U as __pageData,W as default};
