import{_ as o,a as t}from"./chunks/OtherThemesUserMenus.jzfExzta.js";import{_ as i,c as s,o as r,a6 as e}from"./chunks/framework.dp3vP9A2.js";const f=JSON.parse('{"title":"ویژگی‌های سمت کاربر - تبدیل سایت‌های استاتیک به پلتفرم‌های پویا","description":"","frontmatter":{},"headers":[],"relativePath":"fa/guide/user-overview.md","filePath":"fa/guide/user-overview.md","lastUpdated":1737127002000}'),l={name:"fa/guide/user-overview.md"},a=e('<h1 id="ویژگی‌های-سمت-کاربر-تبدیل-سایت‌های-استاتیک-به-پلتفرم‌های-پویا" tabindex="-1">ویژگی‌های سمت کاربر - تبدیل سایت‌های استاتیک به پلتفرم‌های پویا <a class="header-anchor" href="#ویژگی‌های-سمت-کاربر-تبدیل-سایت‌های-استاتیک-به-پلتفرم‌های-پویا" aria-label="Permalink to &quot;ویژگی‌های سمت کاربر - تبدیل سایت‌های استاتیک به پلتفرم‌های پویا&quot;">​</a></h1><p>VpAdmin به شما این امکان را می‌دهد تا با ادغام بی‌وقفه مجموعه‌ای از ویژگی‌های کاربرمحور، وب‌سایت‌های استاتیک را به پلتفرم‌های پویا تبدیل کنید. وقتی عملکرد <strong>Admin</strong> را هنگام ایجاد وب‌سایت خود فعال می‌کنید، سایت شما به‌طور خودکار با احراز هویت کاربر، مدیریت سفارش و ویژگی‌های پرداخت یکپارچه می‌شود و تجربه تعاملی‌تر و غنی‌تر را برای بازدیدکنندگان شما فراهم می‌کند.</p><h2 id="ویژگی‌های-کلیدی-سمت-کاربر" tabindex="-1">ویژگی‌های کلیدی سمت کاربر <a class="header-anchor" href="#ویژگی‌های-کلیدی-سمت-کاربر" aria-label="Permalink to &quot;ویژگی‌های کلیدی سمت کاربر&quot;">​</a></h2><p>VpAdmin ویژگی‌های کلیدی زیر را در سمت کاربر برای وب‌سایت شما فراهم می‌کند:</p><ul><li><p><strong>احراز هویت کاربر:</strong></p><ul><li><strong>ورود/ثبت نام:</strong> کاربران می‌توانند با استفاده از آدرس ایمیل، رمز عبور یا حساب‌های شبکه‌های اجتماعی از طریق پلتفرم‌هایی مانند Google، Facebook، GitHub و GitLab وارد سیستم شوند یا ثبت نام کنند.</li><li><strong>بازیابی رمز عبور:</strong> کاربران در صورت نیاز می‌توانند به راحتی رمزهای عبور خود را بازنشانی کنند.</li></ul></li><li><p><strong>مدیریت سفارش:</strong></p><ul><li><strong>تاریخچه سفارش:</strong> کاربران می‌توانند جزئیات خریدهای قبلی، وضعیت پرداخت و پیام‌های مدیر سایت را مشاهده کنند.</li><li><strong>پیگیری وضعیت:</strong> کاربران می‌توانند وضعیت سفارش‌های خود را پیگیری کنند.</li></ul></li><li><p><strong>مدیریت پروفایل کاربر:</strong></p><ul><li><strong>مشاهده پروفایل:</strong> کاربران می‌توانند اطلاعات شخصی خود را مشاهده و مدیریت کنند.</li><li><strong>بررسی اجمالی داده‌های شخصی:</strong> کاربران می‌توانند اطلاعات کلیدی حساب خود را مشاهده کنند.</li></ul></li><li><p><strong>پشتیبانی یکپارچه پرداخت:</strong></p><ul><li><strong>دکمه‌های پشتیبانی پیشرفته:</strong> کاربران می‌توانند با استفاده از دکمه‌های پشتیبانی پیشرفته قرار داده شده در سایت شما به گزینه‌های پرداخت دسترسی پیدا کنند.</li><li><strong>کسب درآمد:</strong> کاربران شما می‌توانند به سرعت و به راحتی تراکنش‌های پرداخت را تکمیل کنند و به شما امکان می‌دهد محتوای خود را کسب درآمد کنید.</li></ul></li></ul><div class="info custom-block"><p class="custom-block-title">تم‌های VitePress</p><video width="1080" height="768" muted autoplay loop><source src="'+o+'" type="video/mp4"></video></div><div class="info custom-block"><p class="custom-block-title">تم‌های Hugo, Jekyll</p><video width="1080" height="768" muted autoplay loop><source src="'+t+'" type="video/mp4"></video></div><h2 id="مزایا" tabindex="-1">مزایا <a class="header-anchor" href="#مزایا" aria-label="Permalink to &quot;مزایا&quot;">​</a></h2><ul><li><strong>استفاده آسان برای کاربران:</strong> کاربران در وب‌سایت شما یک تجربه ورود، ثبت نام و پرداخت بدون مشکل دریافت می‌کنند.</li><li><strong>مدیریت آسان برای ادمین:</strong> شما یک رویکرد ساده برای مدیریت کاربران، سفارش‌ها و پرداخت‌ها در وب‌سایت‌های خود دریافت می‌کنید.</li><li><strong>تبدیل استاتیک به پویا:</strong> با VpAdmin می‌توانید به سرعت هر وب‌سایت استاتیک را به یک پلتفرم تعاملی تبدیل کنید.</li><li><strong>قابلیت‌های کسب درآمد:</strong> اکنون می‌توانید با استفاده از داشبورد مدیریت قدرتمند، محتوای خود را به راحتی و ایمن‌تر کسب درآمد کنید.</li><li><strong>افزایش تعامل کاربر:</strong> ارائه یک تجربه روان و حرفه‌ای، احتمال بازگشت بازدیدکنندگان را به کاربران افزایش می‌دهد.</li></ul><p>با فعال کردن ویژگی مدیریت هنگام ایجاد وب‌سایت خود در VpAdmin، شما فوراً از تمام این ویژگی‌ها بهره‌مند می‌شوید و یک پلتفرم پویا ایجاد می‌کنید که تعامل، مشارکت و عملکرد کاربرمحور را تسهیل می‌کند.</p>',10),n=[a];function d(p,g,c,u,_,m){return r(),s("div",null,n)}const b=i(l,[["render",d]]);export{f as __pageData,b as default};
