import{_ as n}from"./chunks/OrdersManagement.DaRRegY6.js";import{_ as a,c as e,I as r,w as s,j as o,a as t,a6 as l,E as d,o as p}from"./chunks/framework.dp3vP9A2.js";const U=JSON.parse('{"title":"주문 관리 및 결제 플랫폼 통합","description":"","frontmatter":{},"headers":[],"relativePath":"ko/guide/orders-management.md","filePath":"ko/guide/orders-management.md","lastUpdated":1737127002000}'),m={name:"ko/guide/orders-management.md"},g=o("h1",{id:"주문-관리-및-결제-플랫폼-통합",tabindex:"-1"},[t("주문 관리 및 결제 플랫폼 통합 "),o("a",{class:"header-anchor",href:"#주문-관리-및-결제-플랫폼-통합","aria-label":'Permalink to "주문 관리 및 결제 플랫폼 통합"'},"​")],-1),u=o("p",null,"VpAdmin은 Ko-fi 및 Buy Me a Coffee와 같은 인기 크리에이터 플랫폼과 원활하게 통합되는 강력한 주문 관리 시스템을 제공하여 워크플로를 간소화하고 중앙 집중식 대시보드에서 주문을 관리할 수 있도록 합니다. 이 문서에서는 이 통합의 기본 원칙과 자체 웹사이트를 게시하지 않고도 VpAdmin의 기능을 활용하는 방법을 설명합니다.",-1),c=o("h2",{id:"통합-원칙",tabindex:"-1"},[t("통합 원칙 "),o("a",{class:"header-anchor",href:"#통합-원칙","aria-label":'Permalink to "통합 원칙"'},"​")],-1),_=o("p",null,"Ko-fi와 같은 플랫폼과의 VpAdmin 통합은 웹훅 메커니즘을 기반으로 합니다. 다음은 프로세스 분석입니다.",-1),h=o("p",null,[o("strong",null,"다음 다이어그램은 프로세스를 보여줍니다.")],-1),f=o("a",{href:"/vpadmin/OrdersManagement.png",class:"zoomable-image"},[o("img",{src:n,alt:"주문 관리 프로세스"})],-1),V=l('<ol><li><strong>관리 기능이 있는 웹사이트 만들기:</strong> VpAdmin에서 관리 기능이 활성화된 웹사이트(또는 이 문서 뒷부분에서 설명하는 &quot;가상 웹사이트&quot;)를 만들고 웹사이트에 지원 버튼을 추가할 수 있습니다.</li><li><strong>결제 설정 구성:</strong> VpAdmin에서 &quot;결제&quot; 설정으로 이동하여 결제 구성을 추가합니다. 여기에는 Ko-fi 계정 연결 및 필요한 세부 정보 설정이 포함됩니다.</li><li><strong>웹훅 URL 설정:</strong> Ko-fi 계정에서 웹훅 URL이 VpAdmin을 가리키도록 구성합니다. 이 URL은 웹사이트에 특정하며 웹사이트의 클라이언트 ID를 포함합니다. (예: <code>https://api.vpadmin.org/prod-api/vpadmin/orders/callback/VpadminClientID</code>).</li><li><strong>사용자 결제 시작:</strong><ul><li>사용자가 웹사이트 또는 지원 버튼이 있는 콘텐츠를 방문하여 Ko-fi에서 결제하기 위해 지원 버튼을 클릭합니다.</li><li>사용자가 Ko-fi에서 결제를 완료합니다. VpAdmin은 &quot;결제 확인&quot; 페이지에 사용자의 결제 확인을 기록합니다.</li></ul></li><li><strong>웹훅 트리거:</strong> 결제가 성공하면 Ko-fi는 구성된 웹훅 URL을 트리거하여 주문 세부 정보를 VpAdmin으로 보냅니다.</li><li><strong>주문 처리:</strong> VpAdmin은 주문 정보를 수신하고 처리하며 다음과 같이 구성에 따라 작업을 수행합니다. <ul><li><strong>자동 매칭:</strong> VpAdmin은 주문을 4단계에서 기록된 결제 의도와 매칭하려고 시도합니다.</li><li><strong>상태 업데이트:</strong> 주문 상태가 VpAdmin에서 업데이트됩니다(예: &quot;성공&quot;, &quot;실패&quot;, &quot;대기 중&quot;).</li><li><strong>자동 이메일 답장:</strong> 구성된 경우 VpAdmin은 사용자에게 자동 이메일 답장을 보냅니다.</li><li><strong>수동 검토:</strong> 자동 매칭에 실패하거나 구성되지 않은 경우 관리자는 &quot;결제 확인&quot; 페이지에서 주문 상태를 수동으로 검토하고 업데이트할 수 있습니다.</li></ul></li></ol><h2 id="게시된-웹사이트-없이-vpadmin을-사용하여-주문-관리-가상-웹사이트" tabindex="-1">게시된 웹사이트 없이 VpAdmin을 사용하여 주문 관리(가상 웹사이트) <a class="header-anchor" href="#게시된-웹사이트-없이-vpadmin을-사용하여-주문-관리-가상-웹사이트" aria-label="Permalink to &quot;게시된 웹사이트 없이 VpAdmin을 사용하여 주문 관리(가상 웹사이트)&quot;">​</a></h2><p>VpAdmin을 통해 게시된 웹사이트가 없더라도 플랫폼 내에서 &quot;가상 웹사이트&quot;를 만들어 주문 관리 기능을 활용할 수 있습니다. 이를 통해 Ko-fi와 같은 플랫폼의 주문을 통합하고 단일 위치에서 관리할 수 있습니다.</p><p><strong>작동 방식은 다음과 같습니다.</strong></p><ol><li><strong>&quot;가상 웹사이트&quot; 만들기:</strong> VpAdmin에서 새 웹사이트를 만듭니다. 이 웹사이트는 공개적으로 액세스할 필요가 없거나 실제 콘텐츠를 가질 필요가 없습니다. 결제 구성 및 주문 관리를 위한 컨테이너 역할을 합니다.</li><li><strong>관리 기능 활성화:</strong> 이 가상 웹사이트에 관리 기능이 활성화되어 있는지 확인합니다. 그러면 웹훅 구성에 중요한 클라이언트 ID가 제공됩니다.</li><li><strong>결제 설정 구성:</strong> 일반 웹사이트와 마찬가지로 Ko-fi 결제 구성을 이 가상 웹사이트에 추가합니다.</li><li><strong>웹훅 URL 설정:</strong> Ko-fi 계정에서 웹훅 URL을 클라이언트 ID를 포함하여 가상 웹사이트의 콜백 URL을 가리키도록 설정합니다.</li><li><strong>주문 관리:</strong> 이제 사용자가 Ko-fi 링크를 통해 결제하면 주문 세부 정보가 VpAdmin으로 전송됩니다. 그런 다음 &quot;결제 확인&quot; 페이지를 통해 이러한 주문을 관리하고 상태를 업데이트하고 필요에 따라 회신을 보낼 수 있습니다.</li></ol><p>가상 웹사이트를 사용하면 주문 관리를 중앙 집중화하고 이메일 답장을 자동화하며 VpAdmin 플랫폼 내에서 Ko-fi 거래에 대한 귀중한 통찰력을 얻을 수 있습니다.</p><h2 id="vpadmin을-사용한-주문-관리의-이점" tabindex="-1">VpAdmin을 사용한 주문 관리의 이점 <a class="header-anchor" href="#vpadmin을-사용한-주문-관리의-이점" aria-label="Permalink to &quot;VpAdmin을 사용한 주문 관리의 이점&quot;">​</a></h2><ul><li><strong>중앙 집중식 관리:</strong> 여러 플랫폼의 주문을 한 곳에서 관리합니다.</li><li><strong>자동화된 워크플로:</strong> 주문 처리 및 이메일 답장을 자동화합니다.</li><li><strong>향상된 효율성:</strong> 워크플로를 간소화하고 시간을 절약합니다.</li><li><strong>향상된 사용자 경험:</strong> 지지자에게 보다 전문적이고 반응이 빠른 경험을 제공합니다.</li><li><strong>데이터 통찰력:</strong> 판매 및 고객 행동에 대한 통찰력을 얻습니다.</li></ul><h2 id="결론" tabindex="-1">결론 <a class="header-anchor" href="#결론" aria-label="Permalink to &quot;결론&quot;">​</a></h2><p>VpAdmin의 주문 관리 시스템은 Ko-fi와 같은 플랫폼을 사용하는 크리에이터에게 강력한 솔루션을 제공합니다. 게시된 웹사이트가 있든 없든 VpAdmin을 활용하여 워크플로를 단순화하고 작업을 자동화하며 전체 주문 관리 프로세스를 개선할 수 있습니다. 이러한 플랫폼과 통합하고 자동 회신 및 중앙 집중식 주문 추적과 같은 도구를 제공함으로써 VpAdmin은 창의적인 작업에 집중하는 동시에 지지자에게 원활한 경험을 제공할 수 있도록 지원합니다.</p>',10);function q(A,K,b,k,P,v){const i=d("MagnificPopup");return p(),e("div",null,[g,u,c,_,h,r(i,null,{default:s(()=>[f]),_:1}),V])}const I=a(m,[["render",q]]);export{U as __pageData,I as default};
