import{_ as s,a as l}from"./chunks/AddUser.BCAh4dPL.js";import{_ as n,c as e,I as r,w as i,a6 as o,E as _,o as d,j as t}from"./chunks/framework.dp3vP9A2.js";const S=JSON.parse('{"title":"VpAdmin: 사용자 관리 - 종합 안내","description":"","frontmatter":{},"headers":[],"relativePath":"ko/guide/users.md","filePath":"ko/guide/users.md","lastUpdated":1737127002000}'),g={name:"ko/guide/users.md"},u=o('<h1 id="vpadmin-사용자-관리-종합-안내" tabindex="-1">VpAdmin: 사용자 관리 - 종합 안내 <a class="header-anchor" href="#vpadmin-사용자-관리-종합-안내" aria-label="Permalink to &quot;VpAdmin: 사용자 관리 - 종합 안내&quot;">​</a></h1><p>VpAdmin의 <strong>사용자 관리</strong> 페이지는 플랫폼 내의 모든 사용자를 관리하기 위한 중앙 집중식 위치를 제공합니다. 여기에는 웹사이트를 통해 등록한 사용자뿐만 아니라 특정 역할과 권한을 가진 내부 사용자가 포함됩니다. 이 페이지를 통해 관리자는 다양한 유형의 사용자에 대한 액세스를 생성, 편집 및 제어할 수 있어 간소화되고 안전한 사용자 경험을 제공할 수 있습니다.</p><h2 id="개요" tabindex="-1">개요 <a class="header-anchor" href="#개요" aria-label="Permalink to &quot;개요&quot;">​</a></h2><p>사용자 관리 페이지에서는 다음을 수행할 수 있습니다.</p><ul><li><strong>사용자 목록 보기:</strong> 사용자 이름, 프로젝트 이름, 이메일 주소, 출처 및 상태를 포함하여 등록된 모든 사용자 목록을 볼 수 있습니다.</li><li><strong>사용자 필터링:</strong> 사용자 이름, 프로젝트 이름, 이메일, 출처, 상태 및 생성 시간을 기준으로 사용자를 필터링합니다.</li><li><strong>사용자 생성:</strong> 콘텐츠와 플랫폼을 관리하기 위해 편집자 및 관리자와 같은 특정 역할을 가진 새 사용자를 추가합니다.</li><li><strong>사용자 편집:</strong> 사용자 세부 정보, 역할 및 권한을 수정합니다.</li><li><strong>사용자 상태 관리:</strong> 사용자 계정을 활성화하거나 비활성화합니다.</li><li><strong>비밀번호 재설정:</strong> 사용자 비밀번호를 재설정합니다.</li><li><strong>사용자 가져오기:</strong> 스프레드시트 파일을 사용하여 사용자 데이터를 대량으로 가져옵니다.</li><li><strong>사용자 내보내기:</strong> 모든 사용자와 관련 정보 목록을 스프레드시트 파일로 내보냅니다.</li><li><strong>사용자 역할 관리:</strong> 사용자에게 역할과 해당 권한을 할당합니다(관리자 및 편집자 역할 포함).</li><li><strong>프로젝트 액세스 할당:</strong> 편집자 사용자에게 특정 프로젝트에 대한 액세스 권한을 부여하여 해당 사이트의 콘텐츠를 관리하고 변경 사항을 게시할 수 있도록 합니다.</li></ul><h2 id="사용자-목록" tabindex="-1">사용자 목록 <a class="header-anchor" href="#사용자-목록" aria-label="Permalink to &quot;사용자 목록&quot;">​</a></h2><p>기본 테이블에는 다음 열과 함께 등록된 모든 사용자가 표시됩니다.</p>',7),p=t("a",{href:"/vpadmin/UserList.png",class:"zoomable-image"},[t("img",{src:s,alt:"사용자 목록"})],-1),h=o('<h3 id="사용-가능한-작업" tabindex="-1">사용 가능한 작업 <a class="header-anchor" href="#사용-가능한-작업" aria-label="Permalink to &quot;사용 가능한 작업&quot;">​</a></h3><ul><li><strong>편집:</strong> &quot;편집&quot; 아이콘을 클릭하여 사용자 세부 정보, 역할 및 프로젝트 액세스 권한(편집자용)을 수정합니다. <ul><li>이 옵션을 통해 관리자는 사용자 정보를 수정하고 역할을 할당하며 특정 웹사이트에 대한 액세스 권한을 제공할 수 있습니다.</li></ul></li><li><strong>비밀번호 재설정:</strong> 사용자의 비밀번호를 재설정하려면 &quot;키&quot; 아이콘을 클릭하십시오.</li><li><strong>삭제:</strong> 사용자를 제거하려면 &quot;삭제&quot; 아이콘을 클릭합니다. (참고: 이 작업은 기본 관리자 계정에서는 수행할 수 없습니다).</li></ul><h2 id="새-사용자-생성" tabindex="-1">새 사용자 생성 <a class="header-anchor" href="#새-사용자-생성" aria-label="Permalink to &quot;새 사용자 생성&quot;">​</a></h2><ol><li><strong>&quot;추가&quot; 클릭:</strong> 새 사용자를 생성하려면 &quot;+ 추가&quot; 버튼을 클릭합니다.</li><li><strong>사용자 세부 정보 입력:</strong> 사용자 이름, 닉네임, 이메일, 비밀번호 및 상태를 포함하여 필요한 정보를 입력합니다.</li><li><strong>역할 할당:</strong> 사용자에게 할당할 역할을 선택합니다.</li><li><strong>프로젝트 액세스 할당(편집자용):</strong> 편집자 사용자를 생성하는 경우 액세스할 수 있는 특정 프로젝트를 선택합니다.</li><li><strong>생성 확인:</strong> 새 사용자를 생성하려면 &quot;확인&quot; 버튼을 클릭합니다.</li></ol>',4),c=t("a",{href:"/vpadmin/AddUser.png",class:"zoomable-image"},[t("img",{src:l,alt:"사용자 목록"})],-1),m=o('<h2 id="사용자-역할" tabindex="-1">사용자 역할 <a class="header-anchor" href="#사용자-역할" aria-label="Permalink to &quot;사용자 역할&quot;">​</a></h2><p>사용자 관리 페이지에서는 다음과 같은 유형의 사용자를 지원합니다.</p><ul><li><p><strong>웹사이트 사용자:</strong> 웹사이트의 로그인 포털을 통해 등록한 사용자입니다.</p></li><li><p><strong>편집자 사용자:</strong> 특정 웹사이트에 대한 액세스 권한을 부여받아 해당 사이트의 콘텐츠를 업데이트하고 게시할 수 있는 사용자입니다. 편집자는 관리 기능에 대한 제한된 액세스 권한을 갖습니다.</p><ul><li>편집자 사용자를 생성하거나 편집할 때 액세스할 수 있는 웹사이트를 선택해야 합니다.</li></ul></li><li><p><strong>관리자 사용자:</strong> 모든 사용자, 웹사이트(선택한 사이트에서 편집자로 활동할 수 있는 기능 포함) 및 시스템 설정을 관리하는 것을 포함하여 VpAdmin의 모든 기능에 액세스할 수 있는 사용자입니다.</p></li></ul><h2 id="결론" tabindex="-1">결론 <a class="header-anchor" href="#결론" aria-label="Permalink to &quot;결론&quot;">​</a></h2><p>VpAdmin의 <strong>사용자 관리</strong> 페이지는 플랫폼에 대한 모든 사용자 액세스를 관리하기 위한 포괄적인 도구 세트를 제공합니다. 특정 역할과 권한을 가진 사용자를 생성하고 관리함으로써 간소화된 사용자 경험과 웹사이트 콘텐츠의 효과적인 관리를 모두 보장할 수 있습니다.</p>',5);function q(f,T,P,b,A,V){const a=_("MagnificPopup");return d(),e("div",null,[u,r(a,null,{default:i(()=>[p]),_:1}),h,r(a,null,{default:i(()=>[c]),_:1}),m])}const C=n(g,[["render",q]]);export{S as __pageData,C as default};
