import{_ as a}from"./chunks/dashboard.rbI9-S5F.js";import{_ as s,c as i,I as t,w as r,a6 as n,j as o,E as d,o as l}from"./chunks/framework.dp3vP9A2.js";const V=JSON.parse('{"title":"Visão Geral do Painel Básico","description":"","frontmatter":{},"headers":[],"relativePath":"pt/guide/basic-dashboard.md","filePath":"pt/guide/basic-dashboard.md","lastUpdated":1737127002000}'),c={name:"pt/guide/basic-dashboard.md"},p=n('<h1 id="visao-geral-do-painel-basico" tabindex="-1">Visão Geral do Painel Básico <a class="header-anchor" href="#visao-geral-do-painel-basico" aria-label="Permalink to &quot;Visão Geral do Painel Básico&quot;">​</a></h1><p>O Painel Básico do VpAdmin oferece uma visão geral rápida das métricas principais e indicadores de desempenho do seu site, permitindo que você monitore seu crescimento e atividade rapidamente.</p><p><strong>Métricas Principais:</strong></p><ul><li><strong>Total de Sites:</strong> Exibe o número total de sites gerenciados atualmente por meio de sua conta VpAdmin.</li><li><strong>Total de Usuários:</strong> Mostra o número total de usuários registrados em todos os seus sites.</li><li><strong>Total de Pedidos:</strong> Indica o número total de pedidos feitos em todos os seus sites, caso tenha integrado a funcionalidade de pagamento.</li><li><strong>Receita Mensal:</strong> Exibe a receita total gerada por seus sites durante o mês atual.</li><li><strong>Valor Médio por Pedido:</strong> Mostra o valor médio por pedido, fornecendo insights sobre o comportamento de gastos dos clientes.</li></ul><p><strong>Gráficos de Tendência:</strong></p><p>O painel inclui gráficos de tendência interativos que visualizam o desempenho do seu site ao longo do tempo.</p><ul><li><strong>Tendência de Crescimento de Usuários:</strong> Acompanhe o aumento de usuários registrados em um período.</li><li><strong>Tendência de Pedidos:</strong> Monitore o número de pedidos feitos ao longo do tempo, ajudando você a identificar tendências e padrões.</li><li><strong>Tendência de Receita:</strong> Veja como sua receita mensal muda ao longo do tempo.</li></ul><p><strong>Recursos Interativos:</strong></p><ul><li><strong>Atualizar Dados:</strong> Use o botão &quot;Atualizar Dados&quot; para atualizar todas as métricas exibidas com as informações mais recentes.</li><li><strong>Pré-visualização de Mais Análises:</strong> Clique no botão &quot;Pré-visualização de mais dados analíticos&quot; para explorar opções de análise avançadas.</li><li><strong>Ir para seus sites:</strong> Clique no botão &quot;Ir para seus sites&quot; para ir diretamente para a seção de gerenciamento do seu site.</li></ul><p><strong>Captura de Tela do Painel:</strong></p>',10),m=o("a",{href:"/vpadmin/dashboard.png",class:"zoomable-image"},[o("img",{src:a,alt:"Captura de Tela do Painel Básico"})],-1),u=o("p",null,"O Painel Básico foi projetado para fornecer informações essenciais de desempenho em um formato amigável, permitindo que você monitore os principais indicadores e tome decisões informadas sobre sua presença online.",-1);function g(_,f,h,b,P,v){const e=d("MagnificPopup");return l(),i("div",null,[p,t(e,null,{default:r(()=>[m]),_:1}),u])}const x=s(c,[["render",g]]);export{V as __pageData,x as default};
