import{_ as s}from"./chunks/dashboard.rbI9-S5F.js";import{_ as r,c as a,I as n,w as i,a6 as e,j as t,E as l,o as g}from"./chunks/framework.dp3vP9A2.js";const A=JSON.parse('{"title":"Обзор Основной Панели Управления","description":"","frontmatter":{},"headers":[],"relativePath":"ru/guide/basic-dashboard.md","filePath":"ru/guide/basic-dashboard.md","lastUpdated":1737127002000}'),d={name:"ru/guide/basic-dashboard.md"},p=e('<h1 id="обзор-основнои-панели-управления" tabindex="-1">Обзор Основной Панели Управления <a class="header-anchor" href="#обзор-основнои-панели-управления" aria-label="Permalink to &quot;Обзор Основной Панели Управления&quot;">​</a></h1><p>Основная панель управления VpAdmin предоставляет краткий обзор ключевых показателей и индикаторов производительности вашего веб-сайта, позволяя вам следить за его ростом и активностью с первого взгляда.</p><p><strong>Ключевые Показатели:</strong></p><ul><li><strong>Всего Сайтов:</strong> Отображает общее количество веб-сайтов, в настоящее время управляемых через вашу учетную запись VpAdmin.</li><li><strong>Всего Пользователей:</strong> Показывает общее количество зарегистрированных пользователей на всех ваших сайтах.</li><li><strong>Всего Заказов:</strong> Указывает общее количество заказов, размещенных на всех ваших сайтах, если вы интегрировали функциональность оплаты.</li><li><strong>Ежемесячный Доход:</strong> Отображает общий доход, полученный через ваши веб-сайты за текущий месяц.</li><li><strong>Средняя Сумма Заказа:</strong> Показывает среднюю сумму за заказ, предоставляя информацию о потребительском поведении.</li></ul><p><strong>Графики Трендов:</strong></p><p>Панель управления включает интерактивные графики трендов, которые визуализируют производительность вашего веб-сайта с течением времени.</p><ul><li><strong>Тренд Роста Пользователей:</strong> Отслеживайте увеличение зарегистрированных пользователей за определенный период.</li><li><strong>Тренд Заказов:</strong> Отслеживайте количество заказов, размещенных с течением времени, помогая вам идентифицировать тренды и закономерности.</li><li><strong>Тренд Дохода:</strong> Смотрите, как меняется ваш ежемесячный доход с течением времени.</li></ul><p><strong>Интерактивные Функции:</strong></p><ul><li><strong>Обновить Данные:</strong> Используйте кнопку «Обновить Данные», чтобы обновить все отображаемые показатели с последней информацией.</li><li><strong>Предварительный Просмотр Дополнительной Аналитики:</strong> Нажмите кнопку «Предварительный просмотр дополнительных аналитических данных», чтобы изучить дополнительные параметры анализа.</li><li><strong>Перейти к Сайтам:</strong> Нажмите кнопку «Перейти к Сайтам», чтобы перейти непосредственно к разделу управления вашим веб-сайтом.</li></ul><p><strong>Скриншот Панели Управления:</strong></p>',10),_=t("a",{href:"/vpadmin/dashboard.png",class:"zoomable-image"},[t("img",{src:s,alt:"Скриншот Основной Панели Управления"})],-1),c=t("p",null,"Основная панель управления предназначена для предоставления основных сведений о производительности в удобном для пользователя формате, позволяя вам отслеживать ключевые показатели и принимать обоснованные решения о вашем присутствии в Интернете.",-1);function u(m,h,f,b,P,V){const o=l("MagnificPopup");return g(),a("div",null,[p,n(o,null,{default:i(()=>[_]),_:1}),c])}const N=r(d,[["render",u]]);export{A as __pageData,N as default};
