import{_ as l,a as n,b as r}from"./chunks/Orders.Dfu65Hsk.js";import{_ as c,c as d,I as s,w as a,j as o,a6 as e,a as i,E as p,o as g}from"./chunks/framework.dp3vP9A2.js";const K=JSON.parse('{"title":"Обзор Заказов","description":"","frontmatter":{},"headers":[],"relativePath":"ru/guide/orders.md","filePath":"ru/guide/orders.md","lastUpdated":1737164379000}'),_={name:"ru/guide/orders.md"},h=e('<h1 id="обзор-заказов" tabindex="-1">Обзор Заказов <a class="header-anchor" href="#обзор-заказов" aria-label="Permalink to &quot;Обзор Заказов&quot;">​</a></h1><p>Страница <strong>Заказы</strong> в VpAdmin предоставляет всесторонний интерфейс для управления платежными заказами, связанными с вашими веб-сайтами. Здесь вы можете просматривать детали заказа, отслеживать статус платежа и вручную переотправлять электронные письма в случае неудачной доставки. Этот модуль помогает вам эффективно управлять и контролировать ваши продажи и подписки.</p><h2 id="обзор" tabindex="-1">Обзор <a class="header-anchor" href="#обзор" aria-label="Permalink to &quot;Обзор&quot;">​</a></h2><p>Страница &quot;Заказы&quot; позволяет вам:</p><ul><li><strong>Просматривать Список Заказов:</strong> Просматривать все заказы с ключевыми деталями, такими как название проекта, номер заказа, время создания, сумма, электронная почта клиента и статус отправки электронной почты.</li><li><strong>Фильтровать Заказы:</strong> Фильтровать заказы по названию проекта, номеру заказа, времени создания и электронной почте клиента.</li><li><strong>Просматривать Детали Заказа:</strong> Просматривать полные детали каждого заказа, включая необработанные данные и связанные настройки.</li><li><strong>Переотправлять Электронные Письма:</strong> Вручную переотправлять электронные письма с подтверждением заказа, если первоначальная доставка не удалась.</li></ul><h2 id="настроика-url-адреса-веб-перехватчика-ko-fi" tabindex="-1">Настройка URL-адреса веб-перехватчика Ko-fi <a class="header-anchor" href="#настроика-url-адреса-веб-перехватчика-ko-fi" aria-label="Permalink to &quot;Настройка URL-адреса веб-перехватчика Ko-fi&quot;">​</a></h2><p>Для управления заказами вашего веб-сайта в VpAdmin вам необходимо настроить URL-адрес веб-перехватчика. В своей учетной записи Ko-fi настройте URL-адрес веб-перехватчика на следующий адрес. Пожалуйста, убедитесь, что вы заменили <code>VpadminClientID</code> на <strong>ID клиента</strong>, который находится в детальном представлении вашего сайта в VpAdmin.</p><div class="danger custom-block"><p class="custom-block-title">URL веб-перехватчика</p><div class="language- vp-adaptive-theme"><button title="Скопировать код" class="copy"></button><span class="lang"></span><pre class="shiki shiki-themes github-light github-dark vp-code" tabindex="0"><code><span class="line"><span>https://api.vpadmin.org/prod-api/vpadmin/orders/callback/VpadminClientID</span></span></code></pre></div></div><div class="warning custom-block"><p class="custom-block-title">ПРИМЕЧАНИЕ</p><p>Только веб-сайты с включенной функцией администратора будут иметь ID клиента.</p></div>',9),u=o("a",{href:"/vpadmin/ClientID.png",class:"zoomable-image"},[o("img",{src:l,alt:"ID клиента"})],-1),m={class:"info custom-block"},f=o("p",{class:"custom-block-title"},"Настройка веб-перехватчика Ko-fi",-1),b=o("a",{href:"/vpadmin/KofiWebhook.png",class:"zoomable-image"},[o("img",{src:n,alt:"Веб-перехватчик Ko-fi"})],-1),k=o("div",{class:"tip custom-block",style:{"padding-top":"8px"}},[o("p",null,[i("Чтобы узнать больше об управлении заказами и принципах автоматических/ручных ответов, пожалуйста, обратитесь к "),o("a",{href:"./orders-management"},"Управление заказами"),i(".")])],-1),v=o("h2",{id:"список-заказов",tabindex:"-1"},[i("Список Заказов "),o("a",{class:"header-anchor",href:"#список-заказов","aria-label":'Permalink to "Список Заказов"'},"​")],-1),q=o("p",null,"Основная таблица отображает все ваши существующие заказы со следующими столбцами:",-1),P=o("a",{href:"/vpadmin/Orders.png",class:"zoomable-image"},[o("img",{src:r,alt:"Заказы"})],-1),V=e('<ul><li><strong>Название Проекта:</strong> Название проекта, связанного с заказом.</li><li><strong>Номер Заказа:</strong> Уникальный идентификатор заказа.</li><li><strong>Время Создания Заказа:</strong> Дата и время создания заказа.</li><li><strong>Тип:</strong> Тип платежа, варианты: <code>Пожертвование</code>, <code>Подписка</code>, на основе типов заказов ko-fi.</li><li><strong>Сумма Заказа:</strong> Общая сумма заказа, включая валюту.</li><li><strong>Электронная Почта Клиента:</strong> Адрес электронной почты клиента, оформившего заказ.</li><li><strong>Статус Отправки Электронной Почты:</strong> Статус электронной почты с подтверждением заказа. Варианты: <ul><li><strong>0: NoMatch:</strong> Не найдено соответствия конфигурации платежа.<div class="warning custom-block"><p class="custom-block-title">ПРИМЕЧАНИЕ</p><p>Чтобы переотправить электронное письмо, пожалуйста, сначала перейдите на страницу &quot;Платежи&quot; и добавьте необходимую конфигурацию платежа. Пожалуйста, прочитайте <a href="./payments">Платежи</a> для получения более подробной информации.</p></div></li><li><strong>1: Успех:</strong> Электронное письмо было успешно отправлено.</li><li><strong>2: Ошибка:</strong> Не удалось отправить электронное письмо.</li><li><strong>3: MatchNoSend:</strong> Соответствующая конфигурация платежа найдена, но автоматическая отправка не включена.</li></ul></li><li><strong>Дополнительное Сообщение Заказа:</strong> Дополнительное сообщение, включенное в платежный заказ.</li><li><strong>Операции:</strong> Предоставляет варианты для управления каждым заказом.</li></ul><h3 id="доступные-операции" tabindex="-1">Доступные Операции: <a class="header-anchor" href="#доступные-операции" aria-label="Permalink to &quot;Доступные Операции:&quot;">​</a></h3><ul><li><strong>Детали:</strong> Нажмите на значок документа, чтобы просмотреть необработанные данные, связанные с заказом.</li><li><strong>Переотправить Электронное Письмо:</strong> Нажмите на значок переотправки электронной почты, чтобы вручную переотправить электронное письмо, если оно не было отправлено автоматически.</li></ul><div class="warning custom-block"><p class="custom-block-title">ПРИМЕЧАНИЕ</p><p>Содержание и тема электронного письма настраиваются на странице <code>Платежи</code>. Пожалуйста, прочитайте <a href="./payments">Платежи</a> для получения более подробной информации.</p></div><h2 id="заключение" tabindex="-1">Заключение <a class="header-anchor" href="#заключение" aria-label="Permalink to &quot;Заключение&quot;">​</a></h2><p>Страница <strong>Заказы</strong> в VpAdmin предоставляет полное решение для управления вашими платежными заказами, обеспечивая отслеживание всех транзакций и эффективное управление всеми заказами. Объединяя управление заказами с функциональностью переотправки электронных писем, этот модуль помогает поддерживать профессиональный и беспроблемный опыт покупок для ваших пользователей.</p>',6);function x(T,I,A,C,D,N){const t=p("MagnificPopup");return g(),d("div",null,[h,s(t,null,{default:a(()=>[u]),_:1}),o("div",m,[f,s(t,null,{default:a(()=>[b]),_:1})]),k,v,q,s(t,null,{default:a(()=>[P]),_:1}),V])}const U=c(_,[["render",x]]);export{K as __pageData,U as default};
