import{_ as e,a as n,b as r}from"./chunks/PaymentUpdateStatus.DcwPxBzO.js";import{_ as s,c as _,I as o,w as i,a6 as l,j as t,a as d,E as u,o as c}from"./chunks/framework.dp3vP9A2.js";const v=JSON.parse('{"title":"Обзор подтверждений платежей","description":"","frontmatter":{},"headers":[],"relativePath":"ru/guide/payments-confirm.md","filePath":"ru/guide/payments-confirm.md","lastUpdated":1737127002000}'),h={name:"ru/guide/payments-confirm.md"},m=l('<h1 id="обзор-подтверждении-платежеи" tabindex="-1">Обзор подтверждений платежей <a class="header-anchor" href="#обзор-подтверждении-платежеи" aria-label="Permalink to &quot;Обзор подтверждений платежей&quot;">​</a></h1><p>Страница <strong>Подтверждения платежей</strong> в VpAdmin предоставляет исчерпывающий обзор подтверждений платежей, инициированных с ваших веб-сайтов. Эта страница позволяет администраторам отслеживать платежные операции, управлять статусами заказов и отвечать пользователям, способствуя эффективной обработке транзакций и улучшению поддержки пользователей.</p><h2 id="обзор" tabindex="-1">Обзор <a class="header-anchor" href="#обзор" aria-label="Permalink to &quot;Обзор&quot;">​</a></h2><p>Страница подтверждений платежей позволяет вам:</p><ul><li><strong>Отслеживать подтверждения платежей:</strong> Просматривать список всех подтверждений платежей, полученных с ваших веб-сайтов.</li><li><strong>Фильтровать подтверждения платежей:</strong> Фильтровать подтверждения платежей по ID, имени проекта, имени платежа, электронной почте пользователя, статусу и времени.</li><li><strong>Просматривать детали подтверждения платежа:</strong> Просматривать подробную информацию о каждом платеже, включая связанную платежную информацию, электронную почту пользователя, статус и текст ответа.</li><li><strong>Обновлять статус платежа:</strong> Вручную обновлять статус платежа и устанавливать сообщение ответа для пользователя.</li><li><strong>Пакетно обновлять статусы платежей:</strong> Обновлять статус нескольких записей платежей одновременно.</li></ul><h2 id="список-подтверждении-платежеи" tabindex="-1">Список подтверждений платежей <a class="header-anchor" href="#список-подтверждении-платежеи" aria-label="Permalink to &quot;Список подтверждений платежей&quot;">​</a></h2><p>В основной таблице отображаются все записи подтверждений платежей со следующими столбцами:</p>',7),p=t("a",{href:"/vpadmin/PaymentsConfirmList.png",class:"zoomable-image"},[t("img",{src:e,alt:"Список подтверждений платежей"})],-1),g=l('<ul><li><strong>ID:</strong> Уникальный идентификатор записи подтверждения платежа.</li><li><strong>Название проекта:</strong> Название проекта, связанного с платежом.</li><li><strong>Название платежа:</strong> Название используемой конфигурации платежа.</li><li><strong>Электронная почта:</strong> Адрес электронной почты пользователя, инициировавшего платеж.</li><li><strong>Статус:</strong> Текущий статус платежа, с вариантами: <ul><li>Ожидание: Система получила подтверждение платежа и ждет, пока система сопоставит платеж.</li><li>Успех: Платеж завершен и сопоставлен с заказом kofi.</li><li>Неудачно: Сопоставление подтверждения платежа не удалось</li></ul></li><li><strong>Время:</strong> Время создания записи подтверждения платежа.</li><li><strong>Операции:</strong> Предоставляет варианты для управления каждой записью подтверждения платежа.</li></ul><h3 id="доступные-операции" tabindex="-1">Доступные операции <a class="header-anchor" href="#доступные-операции" aria-label="Permalink to &quot;Доступные операции&quot;">​</a></h3><ul><li><strong>Просмотреть детали:</strong> Нажмите на значок &quot;Просмотр&quot;, чтобы просмотреть детали подтверждения платежа.</li><li>Диалоговое окно с подробностями содержит следующую информацию:</li></ul>',3),f=t("a",{href:"/vpadmin/PaymentConfirmDetail.png",class:"zoomable-image"},[t("img",{src:n,alt:"Детали подтверждения платежа",style:{width:"50%"}})],-1),q=t("ul",null,[t("li",null,[t("strong",null,"Обновить статус:"),d(' Нажмите на значок "Редактировать", чтобы изменить статус подтверждения платежа и добавить сообщение ответа.')]),t("li",null,"Это открывает диалоговое окно, которое позволяет вам обновить статус и предоставить настраиваемые ответы пользователю.")],-1),b=t("a",{href:"/vpadmin/PaymentUpdateStatus.png",class:"zoomable-image"},[t("img",{src:r,alt:"Обновить статус платежа",style:{width:"50%"}})],-1),P=l('<h2 id="основнои-рабочии-процесс" tabindex="-1">Основной рабочий процесс <a class="header-anchor" href="#основнои-рабочии-процесс" aria-label="Permalink to &quot;Основной рабочий процесс&quot;">​</a></h2><h3 id="автоматическое-сопоставление-платежеи-и-ответ" tabindex="-1">Автоматическое сопоставление платежей и ответ <a class="header-anchor" href="#автоматическое-сопоставление-платежеи-и-ответ" aria-label="Permalink to &quot;Автоматическое сопоставление платежей и ответ&quot;">​</a></h3><ol><li><strong>Пользователь инициирует платеж:</strong> Когда пользователь совершает платеж, используя кнопку поддержки на вашем веб-сайте, VpAdmin регистрирует подтверждение платежа пользователя на странице &quot;Подтверждения платежей&quot;.</li><li><strong>Обработка веб-хука:</strong> Если платеж от Ko-fi успешен и соответствует конфигурации на странице конфигурации платежа, VpAdmin попытается автоматически сопоставить заказ с платежом. <ul><li>Если найдена соответствующая конфигурация платежа, VpAdmin автоматически изменит статус платежа на &quot;Успех&quot; и предоставит автоматический ответ по электронной почте.</li><li>Если соответствующая конфигурация платежа не найдена, запись платежа будет помечена статусом &quot;Ожидание&quot;.</li></ul></li><li><strong>Требуется ручное действие администратора:</strong> Для записей подтверждения платежа, у которых нет соответствующего заказа и статус &quot;Ожидание&quot;, администраторам может потребоваться вручную изменить статус этих записей платежа и отправить ответ.</li></ol><div class="tip custom-block" style="padding-top:8px;"><p>Чтобы узнать больше об управлении заказами и принципах автоматических/ручных ответов, пожалуйста, обратитесь к <a href="./orders-management">Управление заказами</a>.</p></div><h2 id="пользовательскии-опыт" tabindex="-1">Пользовательский опыт <a class="header-anchor" href="#пользовательскии-опыт" aria-label="Permalink to &quot;Пользовательский опыт&quot;">​</a></h2><p>Пользователи могут просматривать статус и ответ администратора в разделе &quot;Платежи&quot; в пользовательском центре.</p><h2 id="заключение" tabindex="-1">Заключение <a class="header-anchor" href="#заключение" aria-label="Permalink to &quot;Заключение&quot;">​</a></h2><p>Страница <strong>Подтверждения платежей</strong> является важным компонентом для управления подтверждениями платежей в VpAdmin. Предоставляя инструменты для отслеживания, обновления и ответа на платежи, VpAdmin обеспечивает прозрачность и беспрепятственный процесс оплаты как для администраторов, так и для пользователей.</p>',8);function T(V,A,x,k,S,C){const a=u("MagnificPopup");return c(),_("div",null,[m,o(a,null,{default:i(()=>[p]),_:1}),g,o(a,null,{default:i(()=>[f]),_:1}),q,o(a,null,{default:i(()=>[b]),_:1}),P])}const N=s(h,[["render",T]]);export{v as __pageData,N as default};
