import{_ as s,a as l}from"./chunks/AddUser.BCAh4dPL.js";import{_ as n,c as e,I as r,w as i,a6 as o,E as _,o as d,j as t}from"./chunks/framework.dp3vP9A2.js";const k=JSON.parse('{"title":"VpAdmin: Управление пользователями - Подробное руководство","description":"","frontmatter":{},"headers":[],"relativePath":"ru/guide/users.md","filePath":"ru/guide/users.md","lastUpdated":1737127002000}'),g={name:"ru/guide/users.md"},u=o('<h1 id="vpadmin-управление-пользователями-подробное-руководство" tabindex="-1">VpAdmin: Управление пользователями - Подробное руководство <a class="header-anchor" href="#vpadmin-управление-пользователями-подробное-руководство" aria-label="Permalink to &quot;VpAdmin: Управление пользователями - Подробное руководство&quot;">​</a></h1><p>Страница <strong>Управление пользователями</strong> в VpAdmin предоставляет централизованное расположение для управления всеми пользователями в пределах платформы. Сюда входят пользователи, зарегистрированные через ваши веб-сайты, а также внутренние пользователи с определенными ролями и разрешениями. Эта страница позволяет администраторам создавать, редактировать и контролировать доступ для различных типов пользователей, обеспечивая удобство и безопасность пользовательского опыта.</p><h2 id="обзор" tabindex="-1">Обзор <a class="header-anchor" href="#обзор" aria-label="Permalink to &quot;Обзор&quot;">​</a></h2><p>Страница управления пользователями позволяет вам:</p><ul><li><strong>Просмотр списка пользователей:</strong> Просматривайте список всех зарегистрированных пользователей, включая их имена пользователей, названия проектов, адреса электронной почты, источники и статусы.</li><li><strong>Фильтрация пользователей:</strong> Фильтруйте пользователей по имени пользователя, названию проекта, электронной почте, источнику, статусу и времени создания.</li><li><strong>Создание пользователей:</strong> Добавляйте новых пользователей с определенными ролями, такими как редактор и администратор, для управления вашим контентом и платформой.</li><li><strong>Редактирование пользователей:</strong> Изменяйте данные пользователя, роли и разрешения.</li><li><strong>Управление статусом пользователя:</strong> Включайте или отключайте учетные записи пользователей.</li><li><strong>Сброс паролей:</strong> Сбрасывайте пароли пользователей.</li><li><strong>Импорт пользователей:</strong> Импортируйте данные пользователей массово с помощью файла электронной таблицы.</li><li><strong>Экспорт пользователей:</strong> Экспортируйте список всех пользователей и связанной информации в файл электронной таблицы.</li><li><strong>Управление ролями пользователей:</strong> Назначайте роли и соответствующие разрешения пользователям (включая роли администратора и редактора).</li><li><strong>Назначение доступа к проекту:</strong> Предоставляйте доступ к определенным проектам для пользователей-редакторов, позволяя им управлять контентом и публиковать изменения для этих сайтов.</li></ul><h2 id="список-пользователеи" tabindex="-1">Список пользователей <a class="header-anchor" href="#список-пользователеи" aria-label="Permalink to &quot;Список пользователей&quot;">​</a></h2><p>В основной таблице отображаются все зарегистрированные пользователи со следующими столбцами:</p>',7),p=t("a",{href:"/vpadmin/UserList.png",class:"zoomable-image"},[t("img",{src:s,alt:"Список пользователей"})],-1),h=o('<h3 id="доступные-операции" tabindex="-1">Доступные операции <a class="header-anchor" href="#доступные-операции" aria-label="Permalink to &quot;Доступные операции&quot;">​</a></h3><ul><li><strong>Редактировать:</strong> Щелкните значок &quot;Редактировать&quot;, чтобы изменить сведения о пользователе, ролях и доступе к проекту (для редакторов). <ul><li>Эта опция позволяет администраторам изменять информацию о пользователе, назначать роли и предоставлять доступ к определенным веб-сайтам.</li></ul></li><li><strong>Сбросить пароль:</strong> Щелкните значок &quot;Ключ&quot;, чтобы сбросить пароль пользователя.</li><li><strong>Удалить:</strong> Щелкните значок &quot;Удалить&quot;, чтобы удалить пользователя. (Примечание: это действие нельзя выполнить в основной учетной записи администратора).</li></ul><h2 id="создание-нового-пользователя" tabindex="-1">Создание нового пользователя <a class="header-anchor" href="#создание-нового-пользователя" aria-label="Permalink to &quot;Создание нового пользователя&quot;">​</a></h2><ol><li><strong>Щелкните &quot;Добавить&quot;:</strong> Нажмите кнопку &quot;+ Добавить&quot;, чтобы создать нового пользователя.</li><li><strong>Введите данные пользователя:</strong> Заполните необходимую информацию, включая имя пользователя, никнейм, электронную почту, пароль и статус.</li><li><strong>Назначить роли:</strong> Выберите роли, которые вы хотите назначить пользователю.</li><li><strong>Назначить доступ к проекту (для редакторов):</strong> Если вы создаете пользователя-редактора, выберите конкретные проекты, к которым он может получить доступ.</li><li><strong>Подтвердить создание:</strong> Нажмите кнопку &quot;Подтвердить&quot;, чтобы создать нового пользователя.</li></ol>',4),c=t("a",{href:"/vpadmin/AddUser.png",class:"zoomable-image"},[t("img",{src:l,alt:"Список пользователей"})],-1),m=o('<h2 id="роли-пользователеи" tabindex="-1">Роли пользователей <a class="header-anchor" href="#роли-пользователеи" aria-label="Permalink to &quot;Роли пользователей&quot;">​</a></h2><p>Страница управления пользователями поддерживает следующие типы пользователей:</p><ul><li><p><strong>Пользователи веб-сайта:</strong> Это пользователи, которые зарегистрировались через портал входа вашего веб-сайта.</p></li><li><p><strong>Пользователи-редакторы:</strong> Эти пользователи получают доступ к определенным веб-сайтам, что позволяет им обновлять и публиковать содержимое этих сайтов. Редакторы имеют ограниченный доступ к функциям администрирования.</p><ul><li>При создании или редактировании пользователя-редактора вам нужно выбрать веб-сайты, к которым он может получить доступ.</li></ul></li><li><p><strong>Пользователи-администраторы:</strong> Эти пользователи имеют доступ ко всему набору функций VpAdmin, включая управление всеми пользователями, веб-сайтами (с возможностью выступать в качестве редактора на выбранных сайтах) и настройками системы.</p></li></ul><h2 id="заключение" tabindex="-1">Заключение <a class="header-anchor" href="#заключение" aria-label="Permalink to &quot;Заключение&quot;">​</a></h2><p>Страница <strong>Управление пользователями</strong> в VpAdmin предоставляет полный набор инструментов для управления всем пользовательским доступом к вашей платформе. Создавая и управляя пользователями с определенными ролями и разрешениями, вы можете обеспечить как удобство работы пользователей, так и эффективное управление контентом вашего веб-сайта.</p>',5);function q(f,T,P,b,A,V){const a=_("MagnificPopup");return d(),e("div",null,[u,r(a,null,{default:i(()=>[p]),_:1}),h,r(a,null,{default:i(()=>[c]),_:1}),m])}const C=n(g,[["render",q]]);export{k as __pageData,C as default};
