import{_ as i}from"./chunks/PaymentMessage.DPQlu2Ij.js";import{_ as n}from"./chunks/OrderTheme.BGApWwJJ.js";import{_ as o,c as s,I as d,w as a,a6 as r,j as t,E as l,o as h}from"./chunks/framework.dp3vP9A2.js";const M=JSON.parse('{"title":"VpAdmin: Pricing and Plans","description":"","frontmatter":{},"headers":[],"relativePath":"ru/prices/prices.md","filePath":"ru/prices/prices.md","lastUpdated":1737166367000}'),c={name:"ru/prices/prices.md"},m=r('<h1 id="vpadmin-pricing-and-plans" tabindex="-1">VpAdmin: Pricing and Plans <a class="header-anchor" href="#vpadmin-pricing-and-plans" aria-label="Permalink to &quot;VpAdmin: Pricing and Plans&quot;">​</a></h1><p>VpAdmin offers a range of plans designed to meet various needs, from basic website creation to advanced management features. Choose the plan that best fits your requirements and start building your dynamic website today.</p><h2 id="available-plans" tabindex="-1">Available Plans <a class="header-anchor" href="#available-plans" aria-label="Permalink to &quot;Available Plans&quot;">​</a></h2><p>VpAdmin provides three distinct plans:</p><ul><li><strong>Free:</strong> Experience the core functionalities of VpAdmin at no cost. Create a website quickly with zero coding required and experience the convenience of one-click publishing with integrated admin features.</li><li><strong>Plan A ($1/Month or $10/Year):</strong> Step up your website management with increased storage, build time, and enhanced admin capabilities.</li><li><strong>Plan B ($2/Month or $20/Year):</strong> Unlock the full potential of VpAdmin with more resources and full admin capabilities.</li><li><strong>Theme</strong>: See the <a href="#purchasing-integrated-private-or-shared-themes">Purchasing Integrated Private or Shared Themes</a> section for details.</li></ul><h2 id="features-for-different-plans" tabindex="-1">Features for Different Plans <a class="header-anchor" href="#features-for-different-plans" aria-label="Permalink to &quot;Features for Different Plans&quot;">​</a></h2><table tabindex="0"><thead><tr><th>Features</th><th>Free</th><th>Plan A ($1/Month)</th><th>Plan B ($2/Month)</th></tr></thead><tbody><tr><td>Online Editor</td><td>✅</td><td>✅</td><td>✅</td></tr><tr><td>Auto Build</td><td>✅</td><td>✅</td><td>✅</td></tr><tr><td>Auto Publish</td><td>✅</td><td>✅</td><td>✅</td></tr><tr><td>Predefined Themes</td><td>✅</td><td>✅</td><td>✅</td></tr><tr><td>Multi-Projects</td><td>Up to 10</td><td>Unlimited</td><td>Unlimited</td></tr><tr><td>Storage/Project</td><td>20MB</td><td>100MB</td><td>200MB</td></tr><tr><td>Builds/Project/Month</td><td>Up to 20</td><td>Up to 40</td><td>Up to 60</td></tr><tr><td>Basic Dashboard</td><td>✅</td><td>✅</td><td>✅</td></tr><tr><td>Changes list/revert/diff</td><td>✅</td><td>✅</td><td>✅</td></tr><tr><td>Users Management</td><td>✅</td><td>✅</td><td>✅</td></tr><tr><td>Publication History</td><td>✅</td><td>✅</td><td>✅</td></tr><tr><td>Email Notification</td><td>✅</td><td>✅</td><td>✅</td></tr><tr><td>Version Creatiton</td><td>✅</td><td>✅</td><td>✅</td></tr><tr><td>Version History</td><td>✅</td><td>✅</td><td>✅</td></tr><tr><td>Version Rollback</td><td>1/Month</td><td>Unlimited</td><td>Unlimited</td></tr><tr><td>Payment Reply</td><td>✅</td><td>✅</td><td>✅</td></tr><tr><td>OAuths Setting</td><td>✅</td><td>✅</td><td>✅</td></tr><tr><td>Orders</td><td>✅</td><td>✅</td><td>✅</td></tr><tr><td>Email Autoreply</td><td>✅</td><td>✅</td><td>✅</td></tr><tr><td>Payment Setting</td><td>✅</td><td>✅</td><td>✅</td></tr><tr><td>Payment Confirm</td><td>✅</td><td>✅</td><td>✅</td></tr><tr><td>Recovery</td><td>1/Month</td><td>Unlimited</td><td>Unlimited</td></tr><tr><td>Download</td><td>3/Month</td><td>Unlimited</td><td>Unlimited</td></tr><tr><td>Users/Roles Manage</td><td>❌</td><td>Unlimited*</td><td>Unlimited*</td></tr><tr><td>Analytics Dashboard</td><td>--</td><td>--</td><td>--</td></tr><tr><td>Protected Content</td><td>--</td><td>--</td><td>--</td></tr></tbody></table><table tabindex="0"><thead><tr><th></th><th>Notes</th></tr></thead><tbody><tr><td>Unlimited*</td><td>You can add an unlimited number of Admin/Editor role management users to your websites. Editor role users can be granted access to specific websites, allowing them to manage content updates and publications for those sites. For more details, please refer to <a href="./../guide/users#user-roles">User Roles</a>.</td></tr><tr><td>--</td><td>Feature Under development.</td></tr></tbody></table><h2 id="payment-information" tabindex="-1">Payment Information <a class="header-anchor" href="#payment-information" aria-label="Permalink to &quot;Payment Information&quot;">​</a></h2><p>You can purchase upgrades for any number of months for your site that you need.</p>',10),u=t("a",{href:"/vpadmin/PaymentMessage.png",class:"zoomable-image"},[t("img",{src:i,alt:"Payment Message"})],-1),p=r('<div class="danger custom-block"><p class="custom-block-title">NOTE</p><p>The VpAdmin system defaults to Plan A. If you are purchasing Plan B or a theme integration service, please input &quot;PlanB&quot; or &quot;Theme&quot; in the message field.</p></div><h2 id="purchasing-integrated-private-or-shared-themes" tabindex="-1">Purchasing Integrated Private or Shared Themes <a class="header-anchor" href="#purchasing-integrated-private-or-shared-themes" aria-label="Permalink to &quot;Purchasing Integrated Private or Shared Themes&quot;">​</a></h2><p>If you would like to use your own custom website theme or integrate a different public theme with VpAdmin, you can purchase the theme integration service.</p><ul><li><strong>Public Theme Integration:</strong> ($10/theme) Your theme will be added to VpAdmin and made available for use by all VpAdmin users. You will also receive a $10 gift card that can be used to upgrade any of the websites you create.</li><li><strong>Private Theme Integration:</strong> ($20/theme) Your theme will be added to VpAdmin and made available for your use only when creating new websites. You will also receive a $10 gift card that can be used to upgrade any of the websites you create.</li></ul><div class="danger custom-block"><p class="custom-block-title">Warning</p><p>Please ensure that you hold the appropriate copyright or have purchased the necessary rights for the theme. VpAdmin reserves the right to refuse integration and provide a refund if the theme’s copyright permissions do not meet the required standards.</p></div><p>To purchase a theme integration service, please navigate to <code>Settings -&gt; General</code>.</p>',6),g=t("a",{href:"/vpadmin/OrderTheme.png",class:"zoomable-image"},[t("img",{src:n,alt:"Payment Message",style:{width:"50%"}})],-1),f=t("p",null,"After your purchase is complete, you will be contacted by the VpAdmin Support Team. The email you receive will detail how to submit your template files. We will then proceed with the theme integration within 3-7 business days, and your account will be credited with a gift card.",-1);function b(_,y,P,v,w,A){const e=l("MagnificPopup");return h(),s("div",null,[m,d(e,null,{default:a(()=>[u]),_:1}),p,d(e,null,{default:a(()=>[g]),_:1}),f])}const q=o(c,[["render",b]]);export{M as __pageData,q as default};
